create table TB_HIST_TRANSFERENCIA
(
    co_hist_transferencia     int auto_increment,
    dt_cadastro               datetime     null,
    dt_realizado              datetime     null,
    nu_saldo_origem_ant       float(12, 2) null,
    nu_saldo_origem_dep       float(12, 2) null,
    nu_saldo_destino_ant      float(12, 2) null,
    nu_saldo_destino_dep      float(12, 2) null,
    nu_valor_transferido      float(12, 2) null,
    co_conta_bancaria_origem  int          not null,
    co_conta_bancaria_destino int          not null,
    co_usuario                int          not null,
    co_assinante              int          not null,
    primary key (co_hist_transferencia, co_conta_bancaria_origem, co_conta_bancaria_destino, co_usuario)
);

